// src/components/Login.js

import React, { useState } from 'react';
import axios from 'axios';

const Login = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError('');

        try {
            const response = await axios.post('http://localhost:5000/api/users/login', {
                username,
                password,
            });

            // Asegúrate de que el objeto de respuesta tenga la estructura que esperas
            const token = response.data.token;
            
            // Aquí puedes guardar el token en localStorage o en el estado global

        } catch (err) {
            console.error(err);
            setError(err.response ? err.response.data.msg : 'Error en la conexión');
        }
    };

    return (
        <div>
            <h2>Iniciar Sesión</h2>
            <form onSubmit={handleSubmit}>
                <input
                    type="text"
                    placeholder="Usuario"
                    value={username}
                    onChange={(e) => setUsername(e.target.value)}
                />
                <input
                    type="password"
                    placeholder="Contraseña"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                />
                <button type="submit">Iniciar Sesión</button>
                {error && <p>{error}</p>}
            </form>
        </div>
    );
};

export default Login;
