import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

const Dashboard = () => {
  const [projects, setProjects] = useState([]);

  useEffect(() => {
    const fetchProjects = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/projects'); // Asegúrate de que esta ruta esté configurada en tu backend
        setProjects(response.data);
      } catch (error) {
        console.error('Error fetching projects:', error);
      }
    };

    fetchProjects();
  }, []);

  return (
    <div className="dashboard">
      <h1>Dashboard</h1>
      <div className="navigation">
        <Link to="/signup"><button>Sign Up</button></Link>
        <Link to="/login"><button>Login</button></Link>
      </div>
      <h2>Recent Projects</h2>
      <ul>
        {projects.map(project => (
          <li key={project._id}>{project.name}</li> // Asegúrate de que 'title' es un campo existente en tu modelo de proyecto
        ))}
      </ul>
    </div>
  );
};

export default Dashboard;
