import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Dashboard = () => {
  const [users, setUsers] = useState([]);
  const [projects, setProjects] = useState([]);
  const [currentUser, setCurrentUser] = useState(null); // Para el usuario actual
  const [error, setError] = useState(null); // Para manejar errores

  // Función para obtener los usuarios
  const fetchUsers = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/users'); // Cambia esta URL según tu ruta
      setUsers(response.data);
    } catch (err) {
      setError(err.message);
    }
  };

  // Función para obtener los proyectos del usuario actual
  const fetchProjects = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/projects'); // Cambia esta URL según tu ruta
      setProjects(response.data);
    } catch (err) {
      setError(err.message);
    }
  };

  // Función para seguir/unfollow a un usuario
  const handleFollow = async (userId) => {
    try {
      await axios.post(`http://localhost:5000/api/users/${userId}/follow`); // Cambia esta URL según tu ruta
      fetchUsers(); // Vuelve a obtener la lista de usuarios
    } catch (err) {
      setError(err.message);
    }
  };

  // Función para manejar el logout
  const handleLogout = () => {
    // Aquí puedes eliminar el token del almacenamiento local o hacer otra lógica para cerrar sesión
    alert('Logout');
  };

  useEffect(() => {
    fetchUsers();
    fetchProjects();
  }, []);

  return (
    <div className="dashboard-container">
      <h1>Dashboard</h1>
      {error && <p className="error">{error}</p>}
      
      <button onClick={handleLogout}>Logout</button>
      
      <h2>Usuarios</h2>
      <ul>
        {users.map(user => (
          <li key={user._id}>
            <p>{user.username}</p>
            <button onClick={() => handleFollow(user._id)}>Follow</button>
            {/* Muestra los proyectos del usuario */}
            <h4>Proyectos:</h4>
            <ul>
              {user.projects.map(project => (
                <li key={project._id}>
                  <strong>{project.name}</strong>: {project.description}
                </li>
              ))}
            </ul>
          </li>
        ))}
      </ul>

      <h2>Mis Proyectos</h2>
      <ul>
        {projects.map(project => (
          <li key={project._id}>
            <strong>{project.name}</strong>: {project.description}
          </li>
        ))}
      </ul>

      {/* Aquí puedes agregar un botón para subir nuevos proyectos */}
      <button onClick={() => alert('Subir nuevo proyecto')}>Subir Proyecto</button>
    </div>
  );
};

export default Dashboard;
